package com.example.hw3_3_m3;

import android.os.Bundle;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import com.example.hw3_3_m3.databinding.FragmentFirstBinding;

import java.util.ArrayList;


public class FirstFragment extends Fragment {

    private FragmentFirstBinding binding;
    private ArrayList<Country> countryes = new ArrayList<>();

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        // Inflate the layout for this fragment
        binding = FragmentFirstBinding.inflate(getLayoutInflater());
        return binding.getRoot();
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        CountryAdapter adapter = new CountryAdapter(countryes);
        binding.rvCountry.setAdapter(adapter);
        initAdapter();
    }

    private void initAdapter() {

        countryes.add(new Country("Kyrgyzstan", "data:image/png;base64,", "Kyrgyzstan, known under Russian and Soviet rule as Kirgiziya, was conquered by tsarist Russian forces in the 19th century. Formerly a constituent (union) republic of the U.S.S.R. , Kyrgyzstan declared its independence on August 31, 1991."));
        countryes.add(new Country("Kazakhstan", "data:image/png;base64,", "Kazakhstan, the largest country fully within the Eurasian Steppe, has been a historical crossroads and home to numerous different peoples, states and empires throughout history. Throughout history, peoples on the territory of modern Kazakhstan had nomadic lifestyle, which developed and influenced Kazakh culture."));
        countryes.add(new Country("Turkey", "data:image/png;base64,", "The history of Turkey, understood as the history of the area now forming the territory of the Republic of Turkey, includes the history of both Anatolia (the Asian part of Turkey) and Eastern Thrace (the European part of Turkey). These two previously politically distinct regions came under control of the Roman Empire in the second century BC, eventually becoming the core of the Roman Byzantine Empire. For times predating the Ottoman period, a distinction should also be made between the history of the Turkic peoples, and the history of the territories now forming the Republic of Turkey[1][2] From the time when parts of what is now Turkey were conquered by the Seljuq dynasty, the history of Turkey spans the medieval history of the Seljuk Empire, the medieval to modern history of the Ottoman Empire, and the history of the Republic of Turkey since the 1920s.[1][2]"));
        countryes.add(new Country("Russia", "data:image/png;base64,","The history of Russia begins with the histories of the East Slavs.[1][2] The traditional start date of specifically Russian history is the establishment of the Rus' state in the north in 862, ruled by Varangians.[3][4] In 882, Prince Oleg of Novgorod seized Kiev, uniting the northern and southern lands of the Eastern Slavs under one authority, moving the governance center to Kiev by the end of the 10th century, and maintaining northern and southern parts with significant autonomy from each other. The state adopted Christianity from the Byzantine Empire in 988, beginning the synthesis of Byzantine and Slavic cultures that defined Russian culture for the next millennium. Kievan Rus' ultimately"));
    }
}